start:
	call reset_registers
    call init_video
    mov si, fasel2
	push si
	mov si, fasel1
	push si
    jmp MENU
    

	loop_jogo:
		call leitor ;pega do teclado 
		mov si, posi
		lodsb
		cmp al, '0'
		jne temColed1 
		pop si
		lodsb
		cmp al, 8
		je GameOver
		dec si
		cmp al, 15
		je youWin
		push si
		jmp continueAnadar
		temColed1:
		pop di
		pop si ;ta com a linha 2, dec push s1 e depois push di
		lodsb
		cmp al, 8
		je GameOver
		cmp al, 15
		je youWin
		dec si
		push si
		push di
		continueAnadar:
		;print do quadro
		pop si
		call draw_linha1
		mov di, si
		pop si
		call draw_linha2
		push si
		push di
        
        ;print nave
        mov si, posi
        lodsb
        cmp al, '0'
            jne call_nave2
        call draw_nave1
        jmp fim_turno
        call_nave2:
            call draw_nave2
        fim_turno:
		    call delay100ms
            call delay100ms
		   ; call clear
		jmp loop_jogo
	

MENU:
    
    call draw_background
    mov si, JOGO_NAME
    mov ah, 2
    mov bh, 0
    mov dh, 4
    mov dl, 10
    int 10h
    call printStr

    mov cx, 666
    mov si, PITI
    mov ah, 2
    mov bh, 0
    mov dh, 18
    mov dl, 8
    int 10h
    call printStr

    mov si, PETB
    mov ah, 2
    mov bh, 0
    mov dh, 15
    mov dl, 9
    int 10h
    call printStr

    call getchar;trava a tela e so libera depois de pressionar a tela

    jmp loop_jogo

getchar:
  mov ah, 0x00
  int 16h
  ret

printCommand: ;printa o que está em al
    mov ah, 0xe
    mov bx, 0xE
    mov bh, 0
    int 10h
    ret


printStr:
    lodsb
    cmp al, 0
    je .bixinha2

    call printCommand
    cmp cx, 666
    je printStr
    mov al, ' ' ; move um espaço para printar a palavra separada por espaços
    call printCommand

    jmp printStr

    .bixinha2:
        ret

    delay100ms:              ; 0.1 SEC DELAY
	mov cx, 01h
  	mov dx, 86a0h
  	mov ah, 86h
  	int 15h
  	ret


	GameOver:
	mov si, go
    mov ah, 2
    mov bh, 0
    mov dh, 10
    mov dl, 10
    int 10h
	call printStr
	call delay1s
    mov si, fasel2
	push si
	mov si, fasel1
	push si
    jmp MENU

	youWin:
	mov si, yw
    mov ah, 2
    mov bh, 0
    mov dh, 10
    mov dl, 10
    int 10h
	call printStr
	call delay1s
    mov si, fasel2
	push si
	mov si, fasel1
	push si
    jmp MENU


newPos:
	mov si, posi
	mov di, posi
	lodsb
	cmp al, '0'
	jne makeZero
		mov byte[di], '1'
		ret
	makeZero:
		mov byte[di], '0'
		ret

leitor:

	mov ah,01h 
	int 16h
	jz fim
	
	mov ah,00h
	int 16h

	call newPos
	;mov si, posi
	;call putchar

	fim:
	ret

prints:

  _loop:
    lodsb
    cmp al, 0
    je .endloop
    call putchar
    jmp _loop
  .endloop:
  ret

putchar:
  mov ah, 0ah
  mov bl, 1
  mov bh, 0
  mov cx, 1
  int 10h
  ret

reset_registers:
	xor ax, ax
	mov ds, ax
	mov dx, ax
	
	ret
	
init_video:
    mov ah, 4FH
    mov al, 02H
    mov bx, 10H 
    int 10h 
    mov al, 13h
    mov ah, 0
    int 10h 
    
    ret


draw_linha1:
	lodsb
    call write_bloco_00
	lodsb
    call write_bloco_01
	lodsb
    call write_bloco_02
	lodsb
    call write_bloco_03

	mov cx, 3
	.back1:
		dec si
	loop .back1
	ret

draw_linha2:
	lodsb
    call write_bloco_10
	lodsb
    call write_bloco_11
	lodsb
    call write_bloco_12
	lodsb
    call write_bloco_13

	mov cx, 3
	.back2:
		dec si
	loop .back2
	ret


draw_nave1:
    mov dx, 0
    mov cx, 0
    mov si, navinha
    lodsb
    .for_v:
        .for_h:
            mov ah, 0ch
            int 10h
            lodsb
            inc cx
            cmp cx, 80
                jne .for_h
        mov cx, 0
        inc dx
        cmp dx,100
            jne .for_v
    
    .done:
        ret

draw_nave2:
    mov dx, 100
    mov cx, 0
    mov si, navinha
    lodsb
    .for_v:
